    int  subsequenceCount(String S, String T) {
        String s= " "+S;
        String t= " "+T;
        int[][]dp = new int[t.length()][s.length()];
        int tIdx=0, sIdx=0;
        while (sIdx< s.length()){
            dp[tIdx][sIdx]= 1;
            sIdx++;
        }
        sIdx=0; tIdx=1;
        while (tIdx< t.length()){
            dp[tIdx][sIdx]= 0;
            tIdx++;
        }

        for (tIdx=1; tIdx< t.length(); tIdx++){
            for (sIdx=1; sIdx< s.length(); sIdx++){
                if (s.charAt(sIdx)==t.charAt(tIdx)){
                    dp[tIdx][sIdx]= (dp[tIdx-1][sIdx-1]+ dp[tIdx][sIdx-1])% 1000000007;
                } else {
                    dp[tIdx][sIdx]=dp[tIdx][sIdx-1];
                }
            }
        }

        return dp[dp.length-1][dp[0].length-1];
    }
